    45→export async function registerAuthService(
    46→  projectId: string,
    47→  region: string
    48→): Promise<{ enabled: boolean; tenant_id: string; endpoint: string }> {
    49→  const config = getServiceConfig();
    50→  const endpoint = `${config.authServiceUrl}/api/tenants/${encodeURIComponent(projectId)}`;
