    56→      'Content-Type': 'application/json',
    57→    };
    58→
    59→    // Add service-to-service authentication token if configured
    60→    if (config.authServiceToken) {
    61→      headers['Authorization'] = `Bearer ${config.authServiceToken}`;
    62→    }
    63→
    64→    await axios.post(
    65→      endpoint,
    66→      {
    67→        tenant_id: projectId,
    68→        region,
    69→        created_at: new Date().toISOString(),
    70→      },
    71→      {
    72→        timeout: 10000, // 10 second timeout
