     4→/**
     5→ * Request log entry structure
     6→ * Contains all required fields for audit trail
     7→ */
     8→interface RequestLogEntry {
     9→  correlationId: string;
    10→  projectId?: string;
    11→  method: string;
    12→  path: string;
    13→  statusCode: number;
    14→  duration: number;
    15→  timestamp: string;
    16→}
    17→
    18→/**
    19→ * Extended Express Request to include timing data
    20→ */
    21→declare global {
    22→  namespace Express {
    23→    interface Request {
    24→      _startTime?: number;
    25→      _projectId?: string;
    26→    }
    27→  }
    28→}
    29→
    30→/**
    31→ * Extract project ID from request
    32→ * Checks both JWT-based (req.projectId) and header-based (x-project-id) approaches
    33→ */
    34→function extractProjectId(req: Request): string | undefined {
    35→  // Check JWT-based project ID first (from US-005)
    36→  if (req.projectId) {
    37→    return req.projectId;
    38→  }
    39→
    40→  // Check header-based project ID (x-project-id header)
    41→  const projectHeader = req.headers['x-project-id'];
    42→  if (typeof projectHeader === 'string' && projectHeader.length > 0) {
    43→    return projectHeader;
    44→  }
    45→
    46→  return undefined;
    47→}
    48→
    49→/**
    50→ * Format log entry as JSON string for structured logging
    51→ * This format is compatible with log aggregation tools
    52→ */
    53→function formatLogEntry(entry: RequestLogEntry): string {
    54→  return JSON.stringify(entry);
    55→}
    56→
    57→/**
    58→ * Async log writer that doesn't block the request/response cycle
    59→ * Uses setImmediate to ensure logging happens in the next event loop tick
    60→ *
    61→ * PERFORMANCE: This ensures logging doesn't slow down request handling
    62→ */
    63→function asyncLog(entry: RequestLogEntry): void {
    64→  setImmediate(() => {
    65→    try {
    66→      const logMessage = formatLogEntry(entry);
    67→      console.log(`[RequestLog] ${logMessage}`);
    68→    } catch (error) {
    69→      // Silently fail to avoid breaking requests due to logging errors
    70→      console.error('[RequestLogger] Failed to log request:', error);
    71→    }
    72→  });
    73→}
    74→
    75→/**
    76→ * Request logging middleware (US-008)
    77→ *
    78→ * This middleware:
    79→ * 1. Records start time when request begins
    80→ * 2. Extracts project_id from JWT or header
    81→ * 3. Logs request details on response finish
    82→ * 4. Uses async logging to avoid blocking requests
    83→ * 5. Includes correlation_id for distributed tracing
    84→ *
    85→ * LOGGED FIELDS:
    86→ * - correlationId: Unique request identifier (from US-006)
    87→ * - projectId: Project identifier (from US-005 JWT or x-project-id header)
    88→ * - method: HTTP method (GET, POST, etc.)
    89→ * - path: Request path (excludes query string for security)
    90→ * - statusCode: HTTP response status code
    91→ * - duration: Request processing time in milliseconds
    92→ * - timestamp: ISO 8601 timestamp of request completion
    93→ *
    94→ * SECURITY:
    95→ * - Does not log query parameters (may contain sensitive data)
    96→ * - Does not log request body (may contain sensitive data)
    97→ * - Does not log response body (may contain sensitive data)
    98→ * - Async logging prevents timing attacks via logging delays
    99→ *
   100→ * PERFORMANCE:
   101→ * - Non-blocking async logging
   102→ * - Minimal overhead in request path
   103→ * - Fail-safe logging (errors don't break requests)
   104→ *
   105→ * Usage:
   106→ * ```typescript
   107→ * import { requestLoggerMiddleware } from '@/api/middleware/request-logger.middleware.js';
   108→ * app.use(requestLoggerMiddleware);
   109→ * ```
   110→ *
   111→ * INTEGRATION NOTES:
   112→ * - Must be applied AFTER correlationMiddleware (US-006)
   113→ * - Can be applied before or after JWT middleware (US-005)
   114→ * - Works with both JWT and header-based authentication
   115→ */
   116→export function requestLoggerMiddleware(
   117→  req: Request,
   118→  res: Response,
   119→  next: NextFunction
   120→): void {
   121→  // Record start time for duration calculation
   122→  req._startTime = Date.now();
   123→
   124→  // Extract project ID early (may be from JWT or header)
   125→  req._projectId = extractProjectId(req);
   126→
   127→  // Log the incoming request
   128→  console.log(formatLogWithCorrelation(req, `${req.method} ${req.path}`));
   129→
   130→  // Listen for response finish event
   131→  // This fires when the response has been sent to the client
   132→  res.on('finish', () => {
   133→    // Calculate request duration
   134→    const duration = req._startTime ? Date.now() - req._startTime : 0;
   135→
   136→    // Create log entry with all required fields
   137→    const logEntry: RequestLogEntry = {
   138→      correlationId: req.correlationId || 'unknown',
   139→      projectId: req._projectId,
   140→      method: req.method,
   141→      path: req.path, // Using path instead of url for security (excludes query string)
   142→      statusCode: res.statusCode,
   143→      duration,
   144→      timestamp: new Date().toISOString()
   145→    };
   146→
   147→    // Log asynchronously to avoid blocking
   148→    asyncLog(logEntry);
   149→  });
   150→
   151→  // Continue processing the request
   152→  next();
   153→}
