    29→class DatabasePool {
    30→  private pool: Pool | null = null;
    31→  private config: PoolConfig;
    32→
    33→  constructor() {
    34→    this.config = this.getConfig();
    35→  }
    36→
    37→  /**
    38→   * Get database configuration from environment variables
    39→   */
    40→  private getConfig(): PoolConfig {
    41→    const DATABASE_URL = process.env.DATABASE_URL;
    42→
    43→    if (DATABASE_URL) {
    44→      return { connectionString: DATABASE_URL };
    45→    }
    46→
    47→    const host = process.env.AUDIT_LOGS_DB_HOST || 'localhost';
    48→    const port = parseInt(process.env.AUDIT_LOGS_DB_PORT || '5432', 10);
    49→    const database = process.env.AUDIT_LOGS_DB_NAME || 'postgres';
    50→    const user = process.env.AUDIT_LOGS_DB_USER || 'postgres';
    51→    const password = process.env.AUDIT_LOGS_DB_PASSWORD;
    52→
    53→    if (!password) {
    54→      throw new Error(
    55→        'Database password not set. Set DATABASE_URL or AUDIT_LOGS_DB_PASSWORD environment variable.'
    56→      );
    57→    }
    58→
    59→    return {
    60→      host,
    61→      port,
    62→      database,
    63→      user,
    64→      password,
    65→      max: parseInt(process.env.AUDIT_LOGS_DB_POOL_MAX || '20', 10),
    66→      idleTimeoutMillis: parseInt(process.env.AUDIT_LOGS_DB_IDLE_TIMEOUT || '30000', 10),
    67→      connectionTimeoutMillis: parseInt(process.env.AUDIT_LOGS_DB_CONN_TIMEOUT || '2000', 10),
    68→    };
    69→  }
    70→
    71→  /**
    72→   * Get or create connection pool
    73→   */
    74→  getPool(): Pool {
    75→    if (!this.pool) {
    76→      this.pool = new Pool(this.config);
    77→
    78→      // Handle pool errors
