    80→        console.error('Unexpected database pool error:', err);
    81→      });
    82→
    83→      // Log pool connection events
    84→      this.pool.on('connect', () => {
    85→        // Silent on connect to avoid noise
    86→      });
    87→
    88→      this.pool.on('remove', () => {
    89→        // Silent on remove to avoid noise
    90→      });
    91→    }
    92→
    93→    return this.pool;
    94→  }
    95→
