   148→ * Execute a query
   149→ */
   150→export async function query<T extends QueryResultRow = QueryResultRow>(text: string, values?: unknown[]): Promise<QueryResult<T>> {
   151→  return dbPool.query<T>(text, values);
   152→}
   153→
   154→/**
   155→ * Get a client from the pool for transactions
   156→ */
   157→export async function getClient() {
   158→  return dbPool.getClient();
   159→}
   160→
   161→/**
   162→ * Close database connections
   163→ */
   164→export async function closeDatabase(): Promise<void> {
   165→  await dbPool.close();
   166→}
   167→
   168→/**
   169→ * Get database pool statistics
   170→ */
   171→export function getDatabaseStats() {
   172→  return dbPool.getStats();
   173→}
   174→
   175→/**
   176→ * Health check for database connection
   177→ */
   178→export async function healthCheck(): Promise<boolean> {
   179→  try {
