     2→ * Database Connection Pool
     3→ *
     4→ * PostgreSQL connection pool configuration for audit logs service.
     5→ * Provides efficient connection management and query execution.
     6→ *
     7→ * US-001: Create Audit Logs Table - Step 7: Integration
     8→ */
     9→
    10→import { Pool, PoolConfig, QueryResult, QueryResultRow } from 'pg';
    11→
    12→/**
    13→ * Database pool configuration
    14→ */
    15→export interface DatabaseConfig {
    16→  host?: string;
    17→  port?: number;
    18→  database?: string;
    19→  user?: string;
    20→  password?: string;
    21→  max?: number;
    22→  idleTimeoutMs?: number;
    23→  connectionTimeoutMs?: number;
    24→}
    25→
    26→/**
    27→ * Database connection pool singleton
    28→ */
    29→class DatabasePool {
    30→  private pool: Pool | null = null;
    31→  private config: PoolConfig;
    32→
    33→  constructor() {
    34→    this.config = this.getConfig();
    35→  }
    36→
    37→  /**
    38→   * Get database configuration from environment variables
    39→   */
    40→  private getConfig(): PoolConfig {
    41→    const DATABASE_URL = process.env.DATABASE_URL;
    42→
    43→    if (DATABASE_URL) {
    44→      return { connectionString: DATABASE_URL };
    45→    }
    46→
    47→    const host = process.env.AUDIT_LOGS_DB_HOST || 'localhost';
    48→    const port = parseInt(process.env.AUDIT_LOGS_DB_PORT || '5432', 10);
    49→    const database = process.env.AUDIT_LOGS_DB_NAME || 'postgres';
    50→    const user = process.env.AUDIT_LOGS_DB_USER || 'postgres';
    51→    const password = process.env.AUDIT_LOGS_DB_PASSWORD;
    52→
    53→    if (!password) {
    54→      throw new Error(
    55→        'Database password not set. Set DATABASE_URL or AUDIT_LOGS_DB_PASSWORD environment variable.'
    56→      );
    57→    }
    58→
    59→    return {
    60→      host,
    61→      port,
    62→      database,
    63→      user,
    64→      password,
    65→      max: parseInt(process.env.AUDIT_LOGS_DB_POOL_MAX || '20', 10),
    66→      idleTimeoutMillis: parseInt(process.env.AUDIT_LOGS_DB_IDLE_TIMEOUT || '30000', 10),
    67→      connectionTimeoutMillis: parseInt(process.env.AUDIT_LOGS_DB_CONN_TIMEOUT || '2000', 10),
    68→    };
    69→  }
    70→
    71→  /**
    72→   * Get or create connection pool
    73→   */
    74→  getPool(): Pool {
    75→    if (!this.pool) {
    76→      this.pool = new Pool(this.config);
    77→
    78→      // Handle pool errors
    79→      this.pool.on('error', (err) => {
    80→        console.error('Unexpected database pool error:', err);
    81→      });
    82→
    83→      // Log pool connection events
    84→      this.pool.on('connect', () => {
    85→        // Silent on connect to avoid noise
    86→      });
    87→
    88→      this.pool.on('remove', () => {
    89→        // Silent on remove to avoid noise
    90→      });
    91→    }
    92→
    93→    return this.pool;
    94→  }
    95→
    96→  /**
    97→   * Execute a query
    98→   */
    99→  async query<T extends QueryResultRow = QueryResultRow>(text: string, values?: unknown[]): Promise<QueryResult<T>> {
   100→    const pool = this.getPool();
   101→    return pool.query<T>(text, values);
   102→  }
   103→
   104→  /**
   105→   * Get a client from the pool for transactions
   106→   */
   107→  async getClient() {
   108→    const pool = this.getPool();
   109→    return pool.connect();
   110→  }
   111→
   112→  /**
   113→   * Close all connections in the pool
   114→   */
   115→  async close(): Promise<void> {
   116→    if (this.pool) {
   117→      await this.pool.end();
   118→      this.pool = null;
   119→    }
   120→  }
   121→
   122→  /**
   123→   * Get pool statistics
   124→   */
   125→  getStats() {
   126→    const pool = this.getPool();
   127→    return {
   128→      totalCount: pool.totalCount,
   129→      idleCount: pool.idleCount,
   130→      waitingCount: pool.waitingCount,
   131→    };
   132→  }
   133→}
   134→
   135→/**
   136→ * Global database pool instance
   137→ */
   138→const dbPool = new DatabasePool();
   139→
   140→/**
   141→ * Get the database pool instance
   142→ */
   143→export function getPool(): Pool {
   144→  return dbPool.getPool();
   145→}
   146→
   147→/**
   148→ * Execute a query
   149→ */
   150→export async function query<T extends QueryResultRow = QueryResultRow>(text: string, values?: unknown[]): Promise<QueryResult<T>> {
   151→  return dbPool.query<T>(text, values);
   152→}
   153→
   154→/**
   155→ * Get a client from the pool for transactions
   156→ */
   157→export async function getClient() {
   158→  return dbPool.getClient();
   159→}
   160→
   161→/**
   162→ * Close database connections
   163→ */
   164→export async function closeDatabase(): Promise<void> {
   165→  await dbPool.close();
   166→}
   167→
   168→/**
   169→ * Get database pool statistics
   170→ */
   171→export function getDatabaseStats() {
   172→  return dbPool.getStats();
   173→}
   174→
   175→/**
   176→ * Health check for database connection
   177→ */
   178→export async function healthCheck(): Promise<boolean> {
   179→  try {
   180→    const result = await query('SELECT 1 as health_check');
   181→    const row = result.rows[0] as Record<string, unknown> | undefined;
   182→    return row?.health_check === 1;
   183→  } catch {
   184→    return false;
   185→  }
   186→}
